public class Q2ModOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int x = 24;
			
			if (x % 2 == 0) {
				System.out.println(x+ " is an even number");
				
			}
			else {
				System.out.println(x+ " is an not an even number");
			}
	}

}
